function tab(tabs,tab) {
	if ($(tab).is('.selected'))
		return;
	var id = "#" + tab.href.split('#')[1];
	var aList = [];
	var idList = [];
	$("a", tabs).each(function() {
		if (this.href.match(/#/)) {
			alert("hre"+this);
			aList.push(this);
			idList.push("#" + this.href.split('#')[1]);
		}
	});

	for (i in aList) {
		//alert(i);
		if(i != "remove"){
			$(aList[i]).removeClass("selected");
		}else{
			alert(i);
		}
		
	}
	for (i in idList) {
		//alert(i);
		if(i != "remove"){
			$(idList[i]).hide();
		}
	}
	$(tab).addClass("selected");
	$(id).show();
}
